import React, { useState } from 'react';
// import data from '../data';

import emailjs from 'emailjs-com';

function Contact() {
///// i use here hooks 
  const [currentData, setData] = useState({
      fullname: '',
      phonenumber: '',
      email: '',
      message: '',
  });

/* this is send email using emailjs */ 

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('gmail', 'template_59wov9m', e.target,
    'user_XZcX2kLITmngmtgeHcG9E')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
      e.target.reset();
  }




  const InputEvent = (event) => {
      const { name, value } = event.target
 ////// i setData and use here 
      setData((preValue) => {
          return {
              ...preValue,
              [name] : value,
          }
      })

  };

  const formSubmit = (e) => {
      e.preventDefault();
      alert (`My name is ${currentData.fullname}. My mobile number is ${currentData.phonenumber} and email is ${currentData.email}, here is what i want to say ${currentData.message}`)

  };

    return (
        <div className='contact'>

         <div className='my-6'>
             <h1 className='text-center'>Contact Me</h1>

         </div>

         <div className='container contact_div'>
             <div className='row'>
                 <div className='col-md-6 col-10 mx-auto'>
                     <form onSubmit={sendEmail}>
                      <div className="mb-3">
                        <label for="exampleFormControlInput1" className="form-label">Full Name
                        </label>

                        <input type="text" className="form-control" id="exampleFormControlInput1" 
                        name="fullname"
                        value={currentData.fullname}
                        onChange={InputEvent}
                        placeholder="Enter your name" name='to_name'/>
                      </div>

                        <div className="mb-3">
                        <label for="exampleFormControlInput1" className="form-label">Phone Number
                        </label>

                        <input type="number" className="form-control" id="exampleFormControlInput1" 
                        name="phonenumber"
                        value={currentData.phonenumber}
                        onChange={InputEvent}
                        placeholder="Drop your number" name='from_name'/>
                        </div>

                        <div className="mb-3">
                        <label for="exampleFormControlInput1"    className="form-label">Email
                        </label>

                        <input type="email" className="form-control" id="exampleFormControlInput1" 
                        name="email"
                        value={currentData.Email}
                        onChange={InputEvent}
                        placeholder="name@example.com" name='message' />
                        </div>

                        <div className="mb-3">
                        <label for="exampleFormControlTextarea1" className="form-label">Message
                        </label>

                        <textarea class="form-control" id="exampleFormControlTextarea1"
                        rows='3' 
                        name="message"
                        value={currentData.Message}
                        onChange={InputEvent}
                        ></textarea>
                     </div>
                     <div class="col-12">
                        <button class="btn btn-outline-primary" type="submit">Submit form</button>
                    </div>


                     </form>

                 </div>

             </div>

         </div>



        </div>
    );

};






export default Contact;
