import React   from "react";


import {
  FaGithub,

} from "react-icons/fa";


const Services = () => {
  const [header] = React.useState({
    mainHeader: "",
    subHeading: "My Project",
    text:
      `If you want look my project    click  'Go Link'.`,
  });
  const [state] = React.useState([
    {
      id: 1,
      icon: <FaGithub className="commonIcons" />,
      heading: "Food Recipe",
      text: 

      <a target="_blank" rel="noopener noreferrer" href="https://github.com/binodpoudel/React-portfolio"><button type="button" className="btn btn-info">Go Link</button></a> 
    },

    {
      id: 2,
      icon: <FaGithub  className="commonIcons" />,
      heading: "Movie app",
      text:
        `Lorem Ipsum is simply dummy text of the printing typesetting\
        industry. simply dummy`,
    },
    {
      id: 3,
      icon: <FaGithub  className="commonIcons" />,
      heading: "Web Desing",
      text:
        `Lorem Ipsum is simply dummy text of the printing typesetting\
        industry. simply dummyheeeelllll`,
    },
    {
      id: 4,
      icon: <FaGithub  className="commonIcons" />,
      heading: "App Devlopment",
      text:
        `Lorem Ipsum is simply dummy text of the printing typesetting\
        industry. simply dummy`,
    }


  ]);
  return (
    <div className="services">
      <div className="container">
        <div className="services__header">
          <div className="common">
            <h3 className="heading">{header.mainHeader}</h3>
            <h1 className="mainHeader">{header.subHeading}</h1>
            <p className="mainContent">{header.text}</p>
            <div className="commonBorder"></div>
          </div>

          <div className="row bgMain">
            {state.map((info) => (
              <div className="col-4 bgMain">
                <div className="services__box">
                  {info.icon}
                  <div className="services__box-header">{info.heading}</div>
                  <div className="services__box-p">{info.text}</div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Services;
