// import data from '../data';
import { Popover } from '@varld/popover';
import emailjs from 'emailjs-com';

function Contact() {


/* this is send email using emailjs */ 

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('gmail', 'template_59wov9m', e.target,
    'user_XZcX2kLITmngmtgeHcG9E')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
      e.target.reset();
  }

    return (

        <div className='contact'>

         <div className='my-6'>
             <h1 className='text-center'>Contact Me</h1>
         </div>
        {/* from here new code */}

        <div className='container contact_div'>
             <div className='row'>
                 <div className='col-md-6 col-10 mx-auto'>
                     <form onSubmit={sendEmail}>
                      <div className="mb-3">
                        <input type="text" className="form-control" 


                        placeholder="Enter your name" required='name' name='from_name'/>
                      </div>

                        <div className="mb-3">


                        <input type="number" className="form-control" 
                        placeholder="Drop your number" required='number' name='to_name'/>
                        </div>

                        <div className="mb-3"> 

                        <input type="email" className="form-control" 
                        placeholder="name@example.com" required='email' name='from_name' />
                        </div>

                        <div className="mb-3"> 
                        <textarea className="form-control" placeholder='Your message' required='message' name='message'
                        ></textarea>
                     </div>
                     <div className="col-12">
                     <Popover
        popover={({ visible, close }) => {
          return (
            <div className="popover">
              <h5>Hey, I am a popover!</h5>
              <p>I am currently {visible ? 'visible' : 'not visible'}.</p>
              <button onClick={close}>Close me</button>
            </div>
          );
        }}
      >
        <button className="btn btn-outline-primary" type="submit">Submit form</button>
      </Popover>

      <div className="form-popup" id={"myForm"}>
    <Modal open={handleClose} onclose={handleShow} id={'content'}>

    <form react mailto='' className="form-container" >

     <label for="email">Email</label>
    <input type="text" placeholder={"Enter Email"} name={"email"} required />

    <label for={"name"}>Name</label>
    <input type={"text"} placeholder="Enter Name" name={"name"} required />

    <label for={"message"}>Message</label>
<textarea type="textarea" name={"message"} placeholder={"Your Message Here"} maxlength={"6000"} rows={"7"} required></textarea>

    <button type="submit" className={"btn"}>Submit</button>
    <button type="submit" className={"btn cancel"} onclick={handleShow}>Close</button>



    </form> 
       </Modal>

    <button className="open-button" onclick={handleShow}>Contact Me</button>  
    </div>
                     {  /*
                        <button className="btn btn-outline-primary" type="submit">Submit form</button>  */}
                    </div>

                     </form>

                 </div>

             </div>

         </div>

        </div>

    );

};






export default Contact;
