import React, { useState } from 'react';
// import data from '../data';

function Contact() {

  const [currentData, setData] = useState({
      fullname: '',
      phonenumber: '',
      email: '',
      message: '',
  });

  const InputEvent = (event) => {
      const {name, value } = event.target

  }

