  }

    return (
        <>

         <div className='my-5'>
             <h1 className='text-center'>Contact Me</h1>

         </div>

         <div className='container contact_div'>
             <div className='row'>
                 <div className='col-md-6 col-10 mx-auto'>
                     <form onSubmit={formSubmit}>
                      <div className="mb-3">
                        <label for="exampleFormControlInput1"    className="form-label">Full Name
                        </label>

                        <input type="text" className="form-control" id="exampleFormControlInput1" 
                        name="full name"
                        value={currentData.fullName}
                        onChange={InputEvent}
                        placeholder="Enter your name" />
                      </div>

                        <div className="mb-3">
                        <label for="exampleFormControlInput1"    className="form-label">Phone Number
                        </label>

                        <input type="number" className="form-control" id="exampleFormControlInput1" 
                        name="phone"
                        value={currentData.phoneNumber}
                        onChange={InputEvent}
                        placeholder="Drop your number" />
                        </div>

                        <div className="mb-3">
                        <label for="exampleFormControlInput1"    className="form-label">Email
                        </label>

                        <input type="email" className="form-control" id="exampleFormControlInput1" 
                        name="email"
                        value={currentData.Email}
                        onChange={InputEvent}
                        placeholder="name@example.com" />
                        </div>

                        <div className="mb-3">
                        <label for="exampleFormControlTextarea1" className="form-label">Message
                        </label>

                        <textarea class="form-control" id="exampleFormControlTextarea1"
                        rows='3' 
                        name="message"
                        value={currentData.Message}
                        onChange={InputEvent}
                        rows="3"></textarea>
                     </div>
                     <div class="col-12">
                        <button class="btn btn-outline-primary" type="submit">Submit form</button>
                    </div>


                     </form>

                 </div>

             </div>

         </div>



        </>
    );

};

export default Contact;
